const animItems=document.querySelectorAll(".n_animation");if(animItems.length>0){window.addEventListener("scroll",animOnScroll);function animOnScroll(){for(let index=0;index<animItems.length;index++){const a=animItems[index];const d=a.offsetHeight;const c=offset(a).top;const b=4;let animItemPoint=window.innerHeight-d/b;if(d>window.innerHeight){animItemPoint=window.innerHeight-window.innerHeight/b}if((pageYOffset>c-animItemPoint)&&pageYOffset<(c+d)){a.classList.add("n_play-animation")}else{if(!a.classList.contains("n_not-repeat-animation")){a.classList.remove("n_play-animation")}}}}}function offset(a){const b=a.getBoundingClientRect(),d=window.pageXOffset||document.documentElement.scrollLeft,c=window.pageYOffset||document.documentElement.scrollTop;return{top:b.top+c,left:b.left+d}}animOnScroll();